import { useParams } from "react-router-dom";
import Header from "../components/header";
import { useState, useEffect } from "react";


const JobPostDetail = () => {

    const [job, setJob] = useState([]);
    const [error, setError] = useState('');
    const id = useParams()

    useEffect(() => {
        axios
          .get(`http://127.0.0.1:1111/api/v1/edit-jobs/{id}/`)
          .then((response) => {
            setJob(response.data);
          })
          .catch((error) => {
            setError(error.message);
          });
      }, []);

      if (error) {
        return (
          <>
            <Header />
            <p>{error}</p>
          </>
        );
      }


  return (
    <>
      <Header />
      <h2>{job.title} Job Details</h2>
      <div
        className="jobs-post"
        style={{ padding: "20px" }}
      >
        <h3>Job Title: {job.job_title}</h3>
        <p style={{ paddingTop: "10px" }}>{job.job_description}</p>
        <h3 style={{ paddingTop: "5px" }}>Enumeration: {job.enumeration}</h3>
        <small>posted on {date.toUTCString()}</small>
      </div>
    </>
  );
};


export default JobPostDetail;
