import "../pages.css";
import Header from "../components/header";
import profilePic from "../images/profile-pic.png";
import { useState } from "react"; 
import { useRef } from "react";

const Profile = () => {

    const [defaultPic, setPic] = useState(profilePic);


    const ref = useRef();

    const chosePicture = () =>{
      ref.current.click();
      alert(defaultPic);
  };
  // const [profilePic, setProfilePic] = useState(profilePic)

  return (
    <>
      <Header />

      <div className="profile-container">
      <h1>Edit your profile</h1>
        <div onClick={chosePicture} className="profile-pic-container">
          <img alt="profile picture" src={profilePic} />
        </div>
        <form action="" method="POST">
        <input style={{display: "none"}} ref={ref} className="profile-pic-input" type="file" id="pic" />

          <input type="text" name="email" placeholder="email"></input> <br />
          <input type="text" name="firstname" placeholder="first name"></input><br />
          <input type="text" name="lastname" placeholder="last name"></input><br />
          <input type="text" name="expertise" placeholder="expertise"></input><br />
          <button className="register-button">Save changes</button>

        </form>

      </div>
    </>
  );
};

export default Profile;
