import "../pages.css";
import Header from "../components/header";


const JobApplication = () => {
  return (
    <>
      <Header />
      <div className="job-application-container">
        <h1 style={{ marginBottom: "20px" }}>Fill the Application Form for the selected job </h1>
        <form action="https://api/v1/job-applications/" method="POST">
          <input type="text" name="first_name" placeholder="first name"></input> <br />
          <input type="text" name="last_name" placeholder="last name"></input>
          <input type="text" name="cover_letter" placeholder="cover letter"></input>
         <label className="select-cv" for="select-cv" style={{  cursor: "pointer", paddingBottom: "10px", paddingTop: "20px" }}>Select CV</label>
          <input id="select-cv" type="file" accept="application/pdf" name="upload_cv" placeholder="upload cv"></input>

          <br />
          <button className="login-button">Apply</button>
        </form>

      </div>
    </>
  );
}; 

export default JobApplication;
